import './OnlineUsers.scss';
import {User} from '@instinct-prj/interface';
import React, {useEffect, useState} from 'react';
import {Row} from '../../../components/generic/row/Row';
import {UserLayout} from '../../../components/layout/user';
import {Card} from '../../../components/generic/card/Card';
import {Container} from '../../../components/generic/container/Container';
import {Jumbotron} from '../../../components/generic/jumbotron/Jumbotron';
import {UserContainer} from '../../../components/templates/user-container/UserContainer';
import {UserContainerSkeleton} from '../../../components/templates/user-container/UserContainerSkeleton';
import {Column, setURL, userService} from '@instinct-web/core';

setURL('community/online', <OnlineUsers />);

export function OnlineUsers() {
  const [onlineUsers, setOnlineUsers] = useState<User[]>();
  useEffect(() => {
    async function fetchUsers(): Promise<void> {
      const users = await userService.getOnline();
      setOnlineUsers(users);
    }

    fetchUsers();
  }, []);

  return (
    <UserLayout section="online">
      <Jumbotron title="Come join the party!">
        <p>Take a sneak peak at who's already online</p>
      </Jumbotron>
      <Container>
        <Row>
          <Column side="left">
            <Card>
              <div className="members-container">
                {onlineUsers === undefined && (
                  <>
                    <UserContainerSkeleton />
                    <UserContainerSkeleton />
                    <UserContainerSkeleton />
                  </>
                )}
                {onlineUsers?.map(_ => (
                  <UserContainer key={_.id} user={_ as any} />
                ))}
                {onlineUsers?.length === 0 && (
                  <p>It looks like everybody is sleeping!</p>
                )}
              </div>
            </Card>
          </Column>
        </Row>
      </Container>
    </UserLayout>
  );
}
