import { useEffect, useState } from 'react';
import { politicalPartyService } from '../../services/political-party';
export function useFetchPoliticalPartyByID(politicalPartyID, reload = 0) {
  const [politicalParty, setPoliticalParty] = useState();
  useEffect(() => {
    async function fetchPoliticalParty() {
      setPoliticalParty(undefined);
      const data = await politicalPartyService.getByID(Number(politicalPartyID));
      setPoliticalParty(data);
    }

    fetchPoliticalParty();
  }, [politicalPartyID, reload]);
  return politicalParty;
}
