import React, {useContext} from 'react';
import {Row} from '../../components/generic/row/Row';
import {UserLayout} from '../../components/layout/user';
import {setURL, configContext, Icon, PermissionGuard} from '@instinct-web/core';
import {Container} from '../../components/generic/container/Container';
import {MiniJumbotron} from '../../components/generic/mini-jumbotron/MiniJumbotron';
import {Link} from 'wouter';

setURL('guides', <GuideDirectory />);

export function GuideDirectory() {
  const {config} = useContext(configContext);
  return (
    <UserLayout>
      <Container>
        <Row>
          <div className="col-12">
            <MiniJumbotron>
              <Row>
                <div className="col-6">
                  <h1>{config.siteName} Guides</h1>
                  <p>Read guides posted by other users like yourself!</p>
                </div>
                <div className="col-6 text-right">
                  <Link to="/guides/create">
                    <button className="btn btn-success btn-lg">
                      <Icon type="plus-circle" />
                      Create
                    </button>
                  </Link>
                </div>
              </Row>
            </MiniJumbotron>
          </div>
        </Row>
        <Row>
          <div className="col-12 text-warning">
            <p>This feature is coming soon.</p>
          </div>
        </Row>
      </Container>
    </UserLayout>
  );
}
