import Moment from 'moment';
import {Photo} from '@instinct-prj/interface';
import {Avatar, photoService} from '@instinct-web/core';
import React, {useEffect, useState} from 'react';
import {defaultPhotosState, PhotosState} from './';
import {Row} from '../../../components/generic/row/Row';
import {UserLayout} from '../../../components/layout/user';
import {Card} from '../../../components/generic/card/Card';
import {Container} from '../../../components/generic/container/Container';
import {MiniJumbotron} from '../../../components/generic/mini-jumbotron/MiniJumbotron';

import {Loading, setURL} from '@instinct-web/core';

setURL('community/photos', <Photos />);

export function Photos() {
  const [state, setState] = useState<PhotosState>(defaultPhotosState);

  useEffect(() => {
    async function fetchPhotos(): Promise<void> {
      const photos: Photo[] = await photoService.getAll();
      setState({
        photos,
        showSpinner: false,
      });
    }
    fetchPhotos();
  }, []);

  return (
    <UserLayout section="community_photos">
      <Container>
        <Row>
          <div className="col-12">
            <MiniJumbotron>
              <h1>Photos</h1>
              <p>Pictures captured by our users around the world</p>
            </MiniJumbotron>
          </div>
        </Row>
        <Loading isLoading={state.showSpinner}>
          {state.photos.length === 0 && (
            <Row>
              <div className="col-12">
                <Card>
                  <h3>Hmmm...</h3>
                  <p>
                    It looks like there aren't any pictures. Maybe you could be
                    the first!
                  </p>
                </Card>
              </div>
            </Row>
          )}
          <Row>
            {state.photos.map(photo => (
              <div className="col-4 mb-4" key={`photo_${photo.id}`}>
                <Card>
                  <div className="col-12">
                    <img
                      src={photo.imagePath}
                      style={{width: '100%', height: 300}}
                    />
                  </div>
                  <hr />
                  <Row>
                    <div className="col-6">
                      <Avatar look={photo.user!.figure} headOnly />
                      <b>{photo.user!.username}</b>
                    </div>
                    <div className="col-6 text-right">
                      <div className="mt-3">
                        {Moment.unix(photo.createdAt).format('MM/DD/YY')}
                      </div>
                    </div>
                  </Row>
                </Card>
              </div>
            ))}
          </Row>
        </Loading>
      </Container>
    </UserLayout>
  );
}
