import { useEffect, useState } from 'react';
export function createOptionalFetchHook(promise, dependencies, refresh = 0) {
  const [state, setState] = useState();
  useEffect(() => {
    setState(undefined);

    async function fetchData() {
      const data = await promise();
      setState(data);
    }

    if (dependencies) {
      fetchData();
    }
  }, [dependencies, refresh]);
  return state;
}
