import React from 'react';
import {Link, useRoute} from 'wouter';
import {Icon, setURL} from '@instinct-web/core';
import {Row} from '../../components/generic/row/Row';
import {UserLayout} from '../../components/layout/user';
import {Container} from '../../components/generic/container/Container';
import {MiniJumbotron} from '../../components/generic/mini-jumbotron/MiniJumbotron';

setURL('guides/view/:guideID', <GuideEdit />);

export function GuideEdit() {
  const [matched, params] = useRoute<{guideID: string}>(
    '/guides/view/:guideID'
  );

  return (
    <UserLayout>
      <Container>
        <Row>
          <div className="col-12">
            <Link to="/guides">
              <Icon
                className="fa-4x text-white"
                style={{cursor: 'pointer'}}
                type="caret-left"
              />
            </Link>
          </div>
        </Row>
        <Row>
          <div className="col-12">
            <MiniJumbotron>
              <h1>View Guide</h1>
            </MiniJumbotron>
          </div>
        </Row>
        <Row>
          <div className="col-12 text-warning">
            <p>This feature is coming soon.</p>
            <p>Guide {params!.guideID} selected</p>
          </div>
        </Row>
      </Container>
    </UserLayout>
  );
}
